mapper.insert:
INSERT INTO certificate_item (
      item
    , certificate_cd
    , describe
) VALUES (
      #{item}
    , #{certificateCd}
    , #{describe}
)

mapper.update:
UPDATE certificate_item
SET
    describe = #{describe}
WHERE
      item          = #{item}
    AND certificate_cd = #{certificateCd}

mapper.delete:
DELETE FROM certificate_item
WHERE
      item          = #{item}
    AND certificate_cd = #{certificateCd}
