mapper.insert:
INSERT INTO certificate_req (
      cert_req_id
    , user_id
    , certificate_cd
    , request_at
    , expire_at
) VALUES (
      #{certReqId}
    , #{userId}
    , #{certificateCd}
    , #{requestAt}
    , #{expireAt}
)

mapper.update:
UPDATE certificate_req
SET
      user_id       = #{userId}
    , certificate_cd = #{certificateCd}
    , request_at     = #{requestAt}
    , expire_at      = #{expireAt}
WHERE
    cert_req_id = #{certReqId}
