mapper.update:
UPDATE exam_submit_detail
SET
    answer = #{answer}
WHERE
    lct_exam_id = #{lctExamId} AND question_no = #{questionNo} AND enroll_id = #{enrollId}

mapper.delete:
DELETE FROM exam_submit_detail
WHERE
    lct_exam_id = #{lctExamId} AND question_no = #{questionNo} AND enroll_id = #{enrollId}
