mapper.insert:
INSERT INTO lct_graderatio (
    grade_criteira_cd,
    lecture_id,
    ratio
) VALUES (
    #{gradeCriteiraCd},
    #{lectureId},
    #{ratio}
)

mapper.update:
UPDATE lct_graderatio
SET
    ratio = #{ratio}
WHERE
    grade_criteira_cd = #{gradeCriteiraCd} AND lecture_id = #{lectureId}

mapper.delete:
DELETE FROM lct_graderatio
WHERE
    grade_criteira_cd = #{gradeCriteiraCd} AND lecture_id = #{lectureId}
