mapper.insert:
INSERT INTO staff_dept (
  stf_dept_cd
, stf_dept_name
, dept_tele_no
, create_at
, delete_at
) VALUES (
  #{stfDeptCd}
, #{stfDeptName}
, #{deptTeleNo}
, #{createAt}
, #{deleteAt}
)

mapper.update:
UPDATE staff_dept
SET 
  stf_dept_name = #{stfDeptName}
, dept_tele_no  = #{deptTeleNo}
, create_at     = #{createAt}
, delete_at     = #{deleteAt}
WHERE stf_dept_cd = #{stfDeptCd}
