mapper.insert:
INSERT INTO STU_MILITARY (
    STUDENT_NO
    , MILITARY_TYPE_CD
    , JOIN_AT
    , EXIT_AT
) VALUES (
    #{studentNo}
    , #{militaryTypeCd}
    , #{joinAt}
    , #{exitAt}
)

mapper.update:
UPDATE STU_MILITARY
SET
    MILITARY_TYPE_CD = #{militaryTypeCd}
    , JOIN_AT = #{joinAt}
    , EXIT_AT = #{exitAt}
WHERE
    STUDENT_NO = #{studentNo}
