mapper.insert:
INSERT INTO stu_scholar_detail (
  yearterm_cd
, student_no
, scholarship_id
) VALUES (
  #{yeartermCd}
, #{studentNo}
, #{scholarshipId}
)

mapper.update:
UPDATE stu_scholar_detail
SET 
  yearterm_cd = #{yeartermCd}
, student_no  = #{studentNo}
WHERE scholarship_id = #{scholarshipId}
