mapper.insert:
INSERT INTO stu_scholarship (
  yearterm_cd
, student_no
, create_at
) VALUES (
  #{yeartermCd}
, #{studentNo}
, #{createAt}
)

mapper.update:
UPDATE stu_scholarship
SET 
  create_at = #{createAt}
WHERE yearterm_cd = #{yeartermCd}
  AND student_no  = #{studentNo}

mapper.delete:
DELETE FROM stu_scholarship
WHERE yearterm_cd = #{yeartermCd}
  AND student_no  = #{studentNo}
