mapper.update:
UPDATE stu_term_tuition
SET
    tuition_sum = #{tuitionSum},
    scholarship_sum = #{scholarshipSum},
    final_amount = #{finalAmount},
    pay_done_yn = #{payDoneYn},
    pay_done_at = #{payDoneAt}
WHERE
    yearterm_cd = #{yeartermCd}
    AND student_no = #{studentNo}

mapper.delete:
DELETE FROM stu_term_tuition
WHERE
    yearterm_cd = #{yeartermCd}
    AND student_no = #{studentNo}
