mapper.insert:
INSERT INTO univ_record_history (
    change_at,
    record_change_cd,
    student_no
) VALUES (
    #{changeAt},
    #{recordChangeCd},
    #{studentNo}
)

mapper.update:
UPDATE univ_record_history
SET
    change_at = #{changeAt},
    student_no = #{studentNo}
WHERE
    record_change_cd = #{recordChangeCd}
