mapper.insert:

            SELECT 'APPR' || LPAD(SEQ_APPROVAL.NEXTVAL, 11, '0') FROM DUAL

		INSERT INTO APPROVAL (
			  APPROVE_ID
			, PREV_APPROVE_ID
			, USER_ID
			, APPROVE_YNNULL
			, APPROVE_AT
			, COMMENTS
			, ATTACH_FILE_ID
			, APPLY_TYPE_CD
			, APPLICANT_USER_ID
		) VALUES (
			  #{approveId}
			, #{prevApproveId}
			, #{userId}
			, null
			, null
			, #{comments}
			, #{attachFileId}
			, #{applyTypeCd}
			, #{applicantUserId}
		)

mapper.update:
UPDATE APPROVAL a
SET 
	a.APPROVE_YNNULL = #{approveYnnull}
	, a.APPROVE_AT = SYSDATE
WHERE
	a.APPROVE_ID = #{approveId}
